var tzoffset = (new Date()).getTimezoneOffset() * 60000; 
var localISOTime = (new Date(Date.now() - tzoffset));
if (localISOTime.getHours() >= 5 && localISOTime.getHours() < 15)
{
    msg.topic="sofar/auto";
    msg.payload="battery_save";
    node.status({fill:"green", shape:"dot", text:msg.payload})
    setTimeout(() => { node.status({}); }, 3000);
    return msg
}

node.status({fill:"green", shape:"ring", text:msg.payload})
switch (msg.payload)
{
	case "0":
		msg.payload = "Standby"
		break;
	case "1":
		msg.payload = "Check charge"
		break;
	case "2":
		msg.payload = "Charging"
		break;
	case "3":
		msg.payload = "Chaeck discharge"
		break;
	case "4":
		msg.payload = "Discharging"
		break;
	case "5":
		msg.payload = "EPSState"
		break;
	case "6":
		msg.payload = "Fault"
		break;
	case "7":
		msg.payload = "Perminant Fault"
		break;
	default:
		msg.payload = "Unknown"
		break;
}
node.status({fill:"green", shape:"ring", text:msg.payload})
setTimeout(() => { node.status({}); }, 3000);
return msg;
