		break;
	case "1":
		msg.payload = "Check charge"
		break;
	case "2":
		msg.payload = "Charging"
		break;
	case "3":
		msg.payload = "Chaeck discharge"
		break;
	case "4":
		msg.payload = "Discharging"
		break;
	case "5":
		msg.payload = "EPSState"
		break;
	case "6":
		msg.payload = "Fault"
		break;
