		msg.payload = "Perminant Fault"
		break;
	default:
		msg.payload = "Unknown"
		break;
}
node.status({fill:"green", shape:"ring", text:msg.payload})
setTimeout(() => { node.status({}); }, 3000);
return msg;

// How much energy to take from the grid, based on the expected solar energy tomorrow.
// Global variable "predictedSolarTomorrow" is set using data from openweathermap.com.
// If you don't have solar, just don't set the variable and it will be zero.

var solarKwh = global.get("predictedSolarTomorrow");
if (!solarKwh)
    solarKwh=0

// Allow for 5kWh daytime consumption (yours may be higher/lower)
var daytimeConsumption = 5;
solarKwh = Math.max(solarKwh-daytimeConsumption,0); 
var batteryCapacityKwh = 9.6; //kWh

// What percentage of the battery's full capacity will be provided by solar tomorrow?
var solarTomorrow = (solarKwh/batteryCapacityKwh)*100;
// Therefore, how much should we take from the grid tonight?
var chargeTarget = parseInt(100-solarTomorrow);

// If you just want to specify your own charge target, you can overide it here.
// For instance, to charge the battery t0 85%, uncomment the next line.
//chargeTarget=85;

msg.payload=chargeTarget;

node.status({fill:"yellow", shape:"ring", text:chargeTarget+"%"})
setTimeout(() => { node.status({}); }, 3000);
return msg

var chargeTarget = parseInt(msg.payload);
var batterySOC = global.get("sofarBatterySOC");
//calculate timezone offset in milliseconds
var tzoffset = (new Date()).getTimezoneOffset() * 60000; 
var localISOTime = (new Date(Date.now() - tzoffset));
if (localISOTime.getHours() >= 2 && localISOTime.getHours() < 5)
{
    if (batterySOC < chargeTarget)
    {
        msg.topic="sofar/charge";
        msg.payload=3000;
        node.status({fill:"green", shape:"dot", text:"charge"})
    }
    else
    {
        msg.topic="sofar/standby";
        msg.payload=true;
        node.status({fill:"yellow", shape:"ring", text:"standby"})
    }
    setTimeout(() => { node.status({}); }, 3000);
    return msg
}

batteryPercent = parseInt(msg.payload);
